import React, { Component } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: "",
      todo_search: [],
      results: []
    };
    this.onChangeSearch = this.onChangeSearch.bind(this);
    this.checkTicker = this.checkTicker.bind(this);
  }
  onChangeSearch(e) {
    this.setState({ items: e.target.value });
  }
  checkTicker(e) {
    e.preventDefault();
    let sortData = [];
    const itemArray = this.state.items.split(",");
    console.log("itemArray: ", itemArray.length);
    for (let i = 0; i < itemArray.length; i++) {
      axios
        .get(
          `https://api.stocktwits.com/api/2/streams/symbol/${itemArray[i]}.json`
        )
        .then(response => {
          sortData = sortData.concat(response.data.messages);
          console.log("newSort", sortData);
          sortData.sort((a, b) => b.created_at.localeCompare(a.created_at));

          this.setState({ results: sortData });
          console.log("sorted data: ", this.state.results);
        })
        .catch(function(error) {
          console.log(error);
        });
    }
  }
  searchList() {
    return this.state.results.map(function(current) {
      return current.body;
    });
  }
  searchDate() {
    return this.state.results.map(function(current) {
      return current.created_at;
    });
  }
  render() {
    return (
      <div style={{ marginTop: 10 }}>
        <div className="row">
          <div className="col-md-5"></div>
          <div className="col-md-6">
            <h3>Search for Tweets</h3>
          </div>
        </div>
        <form>
          <div className="row">
            <div className="col-md-5"></div>
            <div className="col-md-6">
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  value={this.state.items}
                  onChange={this.onChangeSearch}
                />
                <button onClick={this.checkTicker}>Search</button>
              </div>
            </div>
          </div>
          <table border={1}>
            <tbody>
              {this.state.results.map(function(eachField, index) {
                return (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{eachField.body}</td>
                    <td>{eachField.created_at}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </form>
      </div>
    );
  }
}
