import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import Select from "@material-ui/core/Select";
import menuItem from "@material-ui/core/MenuItem";

class Search extends Component {
  state = {
    searchText: "",
    amount: 15,
    ext: ".json",
    apiUrl: "https://api.stocktwits.com/api/2/streams/symbol",
    results: []
  };
  onTextChange = e => {
    this.setState({ [e.target.name]: e.target.value }, () => {
      fetch(
        "${this.state.apiUrl}/q=${this.state.searchText}${this.state.ext}"
      ).then(data => {
        let mes = data.messages.map(msg => {
          return <div key={msg.body}>{msg.body}</div>;
        });
        this.setState({ tickers: mes });
        console.log("state", JSON.stringify(this.state.tickers[0]));
      });
    });
  };
  render() {
    console.log("check", this.state.results);
    return (
      <div>
        <TextField
          name="searchText"
          value={this.state.searchText}
          onChange={this.onTextChange}
          fullWidth={true}
        />
      </div>
    );
  }
}
export default Search;
