import React, { Component } from "react";
import NavBar from "./components/NavBar";
import TextField from "@material-ui/core/TextField";
import Async from "react-async";
import "./index.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      tickers: []
    };
  }
  componentDidMount() {
    fetch("https://api.stocktwits.com/api/2/streams/symbol/TSLA.json")
      .then(response => {
        return response.json();
      })
      .then(data => {
        let tickers = data.response.map(cursor => {
          return <div key={cursor.response}>{cursor.response.body}</div>;
        });
        this.setState({ tickers: tickers });
        console.log("state", this.state.tickers);
      });
  }
  render() {
    return (
      <div className="container2">
        <div className="container1">{this.state.tickers}</div>
      </div>
    );
  }
}
