import React, { Component } from "react";
import axios from "axios";

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todo_search: [],
      results: []
    };
    this.onChangeSearch = this.onChangeSearch.bind(this);
  }
  onChangeSearch(e) {
    console.log("here in search:");
    axios
      .get("http://localhost:4000/todos")
      .then(res => {
        this.setState({ results: res.data });
      })
      .catch(function(error) {
        console.log(error);
      });
    this.setState({
      todo_search: e.target.value
    });
  }
  searchList() {
    return this.state.results.map(function(currentSearch, i) {
      return <Search search={currentSearch} key={i} />;
    });
  }
  render() {
    return (
      <div style={{ marginTop: 10 }}>
        <h3>Search for Tweets</h3>
        <form>
          <div className="form-group">
            <label>search: </label>
            <input
              type="text"
              className="form-control"
              value={this.state.todo_search}
              onChange={this.onChangeSearch}
            />
            <div>{this.searchList()}</div>
          </div>
        </form>
      </div>
    );
  }
}
