import React, { Component } from "react";
import axios from "axios";

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: "",
      todo_search: [],
      results: []
    };
    this.onChangeSearch = this.onChangeSearch.bind(this);
  }
  onChangeSearch(e) {
    this.setState({ items: e.target.value });
  }
  handleSubmit(e) {
    const itemsArray = this.state.item.split(",");
    this.setState(
      {
        todo_search: itemsArray
      },
      this.checkTicker
    );
  }
  checkTicker = () => {
    console.log("array values: ", this.state.todo_search.length);
    let { tickers } = this.state.todo_search;
    for (let i = 0; i < this.state.todo_search.length; i++) {
      if (
        this.state.todo_search.length !== 0 &&
        this.state.todo_search.length >= 4
      ) {
        console.log("yessssssss: ", this.state.todo_search);
        axios
          .get(
            `https://api.stocktwits.com/api/2/streams/symbol/${this.state.todo_search[i]}.json`
          )
          .then(response => {
            this.setState({ results: response.data.messages });
            console.log("results: ", response.data.messages.body);
          })
          .catch(function(error) {
            console.log(error);
          });
      }
    }
  };
  searchList() {
    return this.state.results.map(function(current) {
      console.log(current);
      return current.body;
    });
  }
  render() {
    return (
      <div style={{ marginTop: 10 }}>
        <h3>Search for Tweets</h3>
        <form>
          <div className="form-group">
            <label>search: </label>
            <input
              type="text"
              className="form-control"
              value={this.state.todo_search}
              onChange={this.onChangeSearch}
            />
            <div>
              <div>
                <button onClick={this.handleSubmit}>Submit</button>
              </div>
              <li>{this.searchList()}</li>
            </div>
          </div>
        </form>
      </div>
    );
  }
}
