import React, { Component } from "react";
import NavBar from "./components/NavBar";
import TextField from "@material-ui/core/TextField";
import Async from "react-async";
import "./index.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      tickers: []
    };
  }
  componentDidMount() {
    this.setState({ loading: true });
    fetch("https://api.stocktwits.com/api/2/streams/user/mahveenran.json")
      .then(response => response.json())
      .then(data => {
        this.setState({
          tickers: data
        });
      });
  }
  render() {
    const text = this.state.loading ? "loading..." : this.state.tickers.user;
    return (
      <div>
        <p>{text}</p>
      </div>
    );
  }
}
export default App;
