import React from "react";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      isLoading: true,
      tickers: []
    };
  }

  componentDidMount() {
    const date = localStorage.getItem("tickersDate");
    const tickersDate = date && new Date(parseInt(date));
    const now = new Date();

    const dataAge = Math.round((now - tickersDate) / (1000 * 60)); // in minutes
    const tooOld = dataAge >= 1;

    if (tooOld) {
      this.fetchData();
    } else {
      console.log(
        `Using data from localStorage that are ${dataAge} minutes old.`
      );
    }
  }

  fetchData() {
    this.setState({
      isLoading: true,
      tickers: []
    });

    fetch("https://api.stocktwits.com/api/2/streams/symbol/AAPL.json")
      .then(response => response.json())
      .then(parsedJSON => {
        let mes = parsedJSON.messages.map(msg => {
          return <div key={msg.messages}>{msg.messages}</div>;
        });
        this.setState({ tickers: mes });
