import React from "react";
import ReactDOM from "react-dom";

const BASE_URL =
  "https://api.stocktwits.com/api/2/streams/user/mahveenran.json";

class DataFetcher extends React.Component {
  state = {
    isLoading: false,
    error: null,
    user: []
  };

  componentDidMount() {
    this.setState({ isLoading: true });
    fetch(this.props.url)
      .then(res => {
        if (res.ok) {
          return res.json();
        } else {
          throw Error("Error fetching user!");
        }
      })
      .then(user => this.setState({ user, isLoading: false }))
      .catch(error => this.setState({ error }));
  }

  render() {
    return this.props.children(this.state);
  }
}

class AppRenderProps extends React.Component {
  render() {
    return (
      <div>
        <h1>With Render Props</h1>
        <DataFetcher url={BASE_URL}>
          {({ error, isLoading, user }) => {
            if (error) {
              return <p style={{ color: "red" }}>{error.message}</p>;
            }

            if (isLoading) {
              return <p>Loading user...</p>;
            }

            return (
              <div>
                <h1>App</h1>
                {user.map(post => (
                  <>
                    <h3>{post.title}</h3>
                    <p>{post.body}</p>
                  </>
                ))}
              </div>
            );
          }}
        </DataFetcher>
      </div>
    );
  }
}

function useDataFetcher(url) {
  const [user, setuser] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    setIsLoading(true);
    fetch(url)
      .then(res => {
        if (res.ok) {
          return res.json();
        } else {
          throw Error("Error fetching user!");
        }
      })
      .then(user => {
        setuser(user);
        setIsLoading(false);
      })
      .catch(error => {
        setError(error);
      });
  }, [url]);

  return { user, isLoading, error };
}

function AppHooks() {
  const { user, isLoading, error } = useDataFetcher(BASE_URL);

  if (error) {
    return <p style={{ color: "red" }}>{error.message}</p>;
  }

  if (isLoading) {
    return <p>Loading user...</p>;
  }

  return (
    <div>
      <h1>App</h1>
      {user.map(post => (
        <>
          <h3>{post.title}</h3>
          <p>{post.body}</p>
        </>
      ))}
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<AppHooks />, rootElement);
