import React, { Component } from "react";
import axios from "axios";

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: "",
      todo_search: [],
      results: []
    };
    this.onChangeSearch = this.onChangeSearch.bind(this);
    this.checkTicker = this.checkTicker.bind(this);
  }
  onChangeSearch(e) {
    this.setState({ items: e.target.value }, this.checkTicker);
  }
  checkTicker() {
    const itemArray = this.state.items.split(",");
    console.log("itemArray: ", itemArray.length);
    // Create a new array based on current state:
    let floors = [...this.state.floors];

    // Add item to it
    floors.push({ value: floorName });

    // Set state
    this.setState({ floors });
    for (let i = 0; i < itemArray.length; i++) {
      axios
        .get(
          `https://api.stocktwits.com/api/2/streams/symbol/${itemArray[i]}.json`
        )
        .then(response => {
          this.setState({ results: response.data.messages[0].body });
        })
        .catch(function(error) {
          console.log(error);
        });
    }
  }
  searchList() {
    return this.state.results.map(function(current) {
      return current;
    });
  }
  searchDate() {
    return this.state.results.map(function(current) {
      return current.created_at;
    });
  }
  render() {
    return (
      <div style={{ marginTop: 10 }}>
        <h3>Search for Tweets</h3>
        <form>
          <div className="form-group">
            <label>search: </label>
            <input
              type="text"
              className="form-control"
              value={this.state.items}
              onChange={this.onChangeSearch}
            />
            <div>this.state.results</div> <br />
          </div>
        </form>
      </div>
    );
  }
}
