import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import Select from "@material-ui/core/Select";
import menuItem from "@material-ui/core/MenuItem";

class Search extends Component {
  state = {
    searchText: "",
    amount: 15,
    apiUrl: "https://api.stocktwits.com/api/2/streams/symbol/TSLA.json",
    results: []
  };
  onTextChange = e => {
    fetch("${this.state.apiUrl}/q=${this.state.searchText}")
      .then(response => response.json())
      .then(parsedJSON => this.setState({ results: parsedJSON.messages }));
  };
  render() {
    return (
      <div>
        <TextField
          name="searchText"
          value={this.state.searchText}
          onChange={this.onTextChange}
          floatingLabelText="Search for Tweets"
          fullWidth={true}
        />
      </div>
    );
  }
}
export default Search;
