import React, { Component } from "react";
import axios from "axios";

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todo_search: "",
      results: []
    };
    this.onChangeSearch = this.onChangeSearch.bind(this);
  }
  onChangeSearch(e) {
    this.setState({ todo_search: e.target.value }, this.checkTicker);
  }
  checkTicker = () => {
    const { todo_search } = this.state.todo_search.length;
    if (todo_search !== 0 && todo_search <= 4) {
      axios
        .get(
          `https://api.stocktwits.com/api/2/streams/symbol/${this.state.todo_search}.json`
        )
        .then(response => {
          this.setState({ results: response.data.messages[0].body });
          console.log("results: ", this.state.results);
        })
        .catch(function(error) {
          console.log(error);
        });
    } else {
      // Throw error 404, beer not found
    }
  };
  searchList() {
    return this.state.results.map(function(current, i) {
      return current;
    });
  }
  render() {
    return (
      <div style={{ marginTop: 10 }}>
        <h3>Search for Tweets</h3>
        <form>
          <div className="form-group">
            <label>search: </label>
            <input
              type="text"
              className="form-control"
              value={this.state.todo_search}
              onChange={this.onChangeSearch}
            />
            <div>{this.state.results}</div>
          </div>
        </form>
      </div>
    );
  }
}
