import React, { Component } from "react";
import axios from "axios";

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todo_search: "",
      results: []
    };
    this.onChangeSearch = this.onChangeSearch.bind(this);
  }
  onChangeSearch(e) {
    axios
      .get("https://api.stocktwits.com/api/2/streams/symbol/TSLA.json")
      .then(response => {
        console.log("here in search:", this.state.todo_search);
        this.setState({ results: response.data.messages[0].body });
      })
      .catch(function(error) {
        console.log(error);
      });
  }
  /*searchList() {
    return this.state.results.map(function(currentSearch, i) {
      return <Search search={currentSearch} key={i} />;
    });
  }*/
  render() {
    return (
      <div style={{ marginTop: 10 }}>
        <h3>Search for Tweets</h3>
        <form>
          <div className="form-group">
            <label>search: </label>
            <input
              type="text"
              className="form-control"
              value={this.state.todo_search}
              onChange={this.onChangeSearch}
            />
            <div>{this.state.todo_search}</div>
          </div>
        </form>
      </div>
    );
  }
}
